// src/components/PhotoView.js
import React, { useState } from 'react';

function PhotoView({ photos, codeAcces, onSubmitOrder }) {
  const [selectedPhoto, setSelectedPhoto] = useState(null); // Photo sélectionnée
  const [name, setName] = useState('');
  const [prenom, setPrenom] = useState('');
  const [exemplaires, setExemplaires] = useState(1); // Nombre d'exemplaires

  // Agrandir la photo lorsqu'elle est cliquée
  const handlePhotoClick = (photo) => {
console.log(`Photo sélectionnée : ${photo}`);
    setSelectedPhoto(photo);
  };
  

  // Soumettre la commande
  const handleSubmit = (e) => {
    e.preventDefault();
    if (!selectedPhoto || !name || !prenom || exemplaires < 1) {
      alert("Veuillez sélectionner une photo, entrer le nom, le prénom, et un nombre d'exemplaires valide.");
      return;
    }

    onSubmitOrder({ codeAcces, name, prenom, photoChosen: selectedPhoto, exemplaires });
  };

  return (
    <div>
      <h2>Photos de la classe {codeAcces}</h2>
      
      {/* Disposition en grille des photos */}
      <div className='grid-container'>
        {photos.map((photo, index) => (
          <img
            key={index}
            src={photo}
            alt={`Photo ${index}`}
            onClick={() => handlePhotoClick(photo)}
            className={selectedPhoto === photo ? 'selected-photo' : ''}
            style={{ cursor: 'pointer', maxWidth: '100%', height: 'auto' }}
          />
        ))}
      </div>

      {/* Formulaire de commande */}
      {selectedPhoto && (
        <div style={{ marginTop: '20px' }}>
          <h3>Photo sélectionnée</h3>
          <img src={selectedPhoto} alt="Selected" style={{ maxWidth: '300px', display: 'block', marginBottom: '20px' }} />
          <form onSubmit={handleSubmit}>
            <div>
              <label>Nom de l'enfant :</label>
              <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />
            </div>
            <div>
              <label>Prénom de l'enfant :</label>
              <input type="text" value={prenom} onChange={(e) => setPrenom(e.target.value)} required />
            </div>
            <div>
              <label>Nombre d'exemplaires :</label>
              <input
                type="number"
                min="1"
                value={exemplaires}
                onChange={(e) => setExemplaires(e.target.value)}
                required
              />
            </div>
            <button type="submit">Commander</button>
          </form>
        </div>
      )}
    </div>
  );
}

export default PhotoView;
