// components/PhotoView.js
import React, { useState } from 'react';
import axios from 'axios';

function PhotoView({ photos, codeAcces }) {
  const [photoChosen, setPhotoChosen] = useState('');
  const [name, setName] = useState('');
  const [prenom, setPrenom] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/classes/order', {
        codeAcces,
        name,
        prenom,
        photoChosen
      });
      alert('Commande enregistrée');
    } catch (err) {
      alert('Erreur lors de la commande');
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <select onChange={(e) => setPhotoChosen(e.target.value)}>
          {photos.map((photo, index) => (
            <option key={index} value={photo}>
              Photo {index + 1}
            </option>
          ))}
        </select>
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
          placeholder="Nom"
        />
        <input
          type="text"
          value={prenom}
          onChange={(e) => setPrenom(e.target.value)}
          placeholder="Prénom"
        />
        <button type="submit">Envoyer</button>
      </form>
    </div>
  );
}

export default PhotoView;
