import React, { useState } from 'react';

function PhotoView({ photos, codeAcces, onSubmitOrder }) {
  const [selectedPhoto, setSelectedPhoto] = useState(null);
  const [name, setName] = useState('');
  const [prenom, setPrenom] = useState('');
  const [exemplaires, setExemplaires] = useState(1);

  const handlePhotoClick = (photo) => {
    setSelectedPhoto(photo);
    setSuccessMessage('');
  };
  

  // commande
  const handleSubmit = (e) => {
    e.preventDefault();
    if (!selectedPhoto || !name || !prenom || exemplaires < 1) {
      alert("Veuillez sélectionner une photo, entrer le nom, le prénom, et un nombre d'exemplaires valide.");
      return;
    }
    onSubmitOrder({ codeAcces, name, prenom, photoChosen: selectedPhoto, exemplaires });
    setSuccessMessage('Votre commande est validée !');
  };

  return (
    <div>
      <h2>Photos de la classe {codeAcces}</h2>
      
      <div className="photoContainer">
        {photos.map((photo, index) => (
          <img
            key={index}
            src={photo}
            alt={`Photo ${index}`}
            onClick={() => {
              handlePhotoClick(photo);
            }}
            className={`photoItem ${selectedPhoto === photo ? 'selectedPhoto' : ''}`}
          />
        ))}
      </div>

      {/* Formulaire de commande */}
      {selectedPhoto && (
        <div className="orderForm">
        <h3>Photo sélectionnée</h3>
        <img src={selectedPhoto} alt="Selected" className="selectedImage" style={{ width: '100%', marginBottom: '20px', borderRadius: '8px' }} />
        
        <form onSubmit={handleSubmit}>
          <div>
            <label>Nom de l'enfant :</label>
            <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />
          </div>
          <div>
            <label>Prénom de l'enfant :</label>
            <input type="text" value={prenom} onChange={(e) => setPrenom(e.target.value)} required />
          </div>
          <div>
            <label>Nombre d'exemplaires :</label>
            <input
              type="number"
              min="1"
              value={exemplaires}
              onChange={(e) => setExemplaires(e.target.value)}
              required
            />
          </div>
          <button type="submit">Commander</button>
        </form>
      </div>      
      )}
      {/* Message de confirmation */}
      {successMessage && (
        <div className="successMessage">
          {successMessage}
        </div>
)}

    </div>
  );
}

export default PhotoView;
