import React, { useState } from 'react';
import axios from 'axios';

function AccessPage() {
  const [code, setCode] = useState('');
  const [photos, setPhotos] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const res = await axios.post('http://localhost:5000/api/classes/access', { codeAcces: code });
      setPhotos(res.data.photos);
      setError('');
    } catch (err) {
      setError('Code invalide');
      setPhotos([]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={code}
          onChange={(e) => setCode(e.target.value)}
          placeholder="Entrez le code d'accÃ¨s"
          className="code-input"  // Ajout de classe CSS pour styliser
        />
        <button type="submit" className="submit-button">Valider</button>
      </form>
      {loading && <p>Chargement...</p>}
      {error && <p>{error}</p>}
      <div className="grid-container">
        {photos.length > 0 && photos.map((photo, index) => (
          <img 
            key={index} 
            src={photo} 
            alt={`Photo ${index}`} 
            className="photItem"  // Classe CSS pour chaque image
          />
        ))}
      </div>
    </div>
  );
}

export default AccessPage;
