// src/App.js
import React, { useState } from 'react';
import AccessPage from './pages/AccessPage';
import PhotoView from './components/PhotoView';
import axios from 'axios';

function App() {
  const [photos, setPhotos] = useState([]);
  const [codeAcces, setCodeAcces] = useState('');

  // Gérer la validation du code d'accès et le chargement des photos
  const handleAccess = (code, photos) => {
console.log('Code d\'accès valide, photos chargées :', photos);
    setCodeAcces(code);
    setPhotos(photos);
  };

  // Gérer la soumission de la commande
  const handleSubmitOrder = async (orderData) => {
    try {
      const res = await axios.post('http://localhost:5000/api/classes/order', orderData);
      alert(res.data.message); // Message de confirmation
      // Reset après soumission
      setPhotos([]);
      setCodeAcces('');
    } catch (err) {
      console.error('Erreur lors de la soumission de la commande', err);
      alert('Erreur lors de la commande. Veuillez réessayer.');
    }
  };

  // Désactiver le clic droit
  const handleContextMenu = (e) => {
    e.preventDefault();
  };

  // Styles explicites pour la mise en page
  const appStyle = {
    maxWidth: '1000px',
    margin: '0 auto',
    padding: '20px',
    backgroundColor: '#f9f9f9',
    textAlign: 'center',
  };

  const containerStyle = {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
  };

  return (
    <div className="App" style={appStyle}>
      <div style={containerStyle}>
        {photos.length === 0 ? (
          <AccessPage onAccess={handleAccess} />
        ) : (
          <PhotoView codeAcces={codeAcces} photos={photos} onSubmitOrder={handleSubmitOrder} />
        )}
      </div>
    </div>

  );
}

export default App;
